mapper.select:
SELECT 
FROM `bk_type`
WHERE 1=1

    AND status = #{status}


    AND id = #{btId}


    AND id IN

        #{item}


ORDER BY t_name

SELECT 
FROM `bk_info`
WHERE 1=1

    AND bk_id = #{bkId}


    AND i.bk_name LIKE CONCAT("%", #{bkName}, "%")


    AND status = #{status}

ORDER BY bk_name

    LIMIT #{page.num}, #{page.size}

SELECT
    i.bk_id, i.bk_name, i.bk_author, i.bk_press, i.bk_price, i.bk_img, i.remark,
    i.status AS bk_info_status, i.create_time, i.update_time
FROM `bk_info` i
INNER JOIN `bk_type_info` ti
ON i.bk_id = ti.bk_id
WHERE 1=1

    AND i.bk_id = #{bkId}


    AND ti.id = #{bkId}


    AND i.bk_name LIKE CONCAT("%", #{bkName}, "%")


    AND i.status = #{status}

ORDER BY i.create_time DESC

    LIMIT #{page.num}, #{page.size}

SELECT 
FROM `bk_detail`
WHERE 1=1

    AND bk_id = #{bkId}


    AND ser_id = #{serId}

ORDER BY create_time DESC

    LIMIT #{page.num}, #{page.size}

SELECT
    d.ser_id, d.bk_id, d.status AS bk_detail_status, d.create_time, d.adm_id,
    i.bk_name, i.bk_author, i.bk_press, i.bk_price, i.bk_img, i.remark
FROM `bk_detail` d
INNER JOIN `bk_info` i
ON d.bk_id = i.bk_id
WHERE 1=1

    AND bk_id = #{bkId}


    AND d.ser_id = #{serId}


    LIMIT #{page.num}, #{page.size}

SELECT COUNT(t.count) FROM ( SELECT COUNT(bk_id) AS `count` FROM `bk_type_info` WHERE 1=1
AND bt_id = #{btId}
GROUP BY bk_id ) t

mapper.update:
UPDATE `bk_info`
SET update_time = now()

    , bk_name = #{bkName}


    , bk_author = #{bkAuthor}


    , bk_press = #{bkPress}


    , bk_price = #{bkPrice}


    , bk_img = #{bkImg}


    , remark = #{remark}


    , status = #{bkInfoStatus}

WHERE bk_id = #{bkId}

mapper.delete:
DELETE FROM `bk_type_info`
WHERE bk_id = #{bkId}
AND bt_id IN

    #{item}

mapper.update:
UPDATE `bk_type`
SET update_time = now()

    , status = #{btStatus}


    , t_name = #{tName}

WHERE id = #{btId}

UPDATE `bk_detail`
SET status = #{status}
WHERE ser_id IN

        #{item}

AND status != #{status}
